import React from 'react'
import '../styles/pages/Game.css';

class Game extends React.Component {
  constructor() {
  super();

  this.state = {
    selected: 'X',
    one: '',
    two: '',
    three : '',
    four: '',
    five: '',
    six: '',
    seven: '',
    eight: '',
    nine: '',
  };
}

handleGameClick = ({ target }) => {
  const { selected } = this.state;
  const { name } = target;
  this.setState({
    [name]: selected,
    selected: selected === 'X' ? 'O' : 'X',
  });
  this.winnerRule();
};

handleSelectO = () => {
this.setState({
  selected: 'O',
  three: 'X'
})
}

winnerRule = () => {
  const { one, two, three, four, five, six, seven, eight, nine } = this.state;
  const newLocal = null;
  one & two & three || one, four, seven || one, five, nine || two, five, eight ||
  three, five, seven || four, five, six || seven, eight, nine === 'O' ? console.log('WIN') : newLocal
}


  render() {
    const { selected, one, two, three, four, five, six, seven, eight, nine } = this.state;

    return (
      <div>

      <header>
        <h1>Jogo da Velha</h1>
      </header>

      <main>
        <div className='container'>
          <div className='difficulty-container'>
        <select
          className="difficulty"
          name="difficulty"
        >
          <option>Jogar contra um amigo</option>
          <option>Fácil</option>
          <option>Médio</option>
          <option>Impossível</option>
        </select>
        </div>
        <div className='players-container'>
        <button
          type='button'
          name='player-1-x'
          className={selected === 'X' ? 'player-1-x-selected' : 'player-1-x'}
          value='X'
          >X
        </button>
        <button
          type='button'
          name='player-2-o'
          className={selected === 'O' ? 'player-2-o-selected' : 'player-2-o'}
          value='O'
          onClick={ this.handleSelectO }
          >O
        </button>
        </div>
        <div>
          <span>Vez de {selected}</span>
        </div>
        <div className='game-container'>
        <div className='game'>
          <button
          type='button'
          name='one'
          className='one'
          disabled={ one.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ one }</button>
          <button
          type='button'
          name='two'
          className='two'
          disabled={ two.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ two }</button>
          <button
          type='button'
          name='three'
          className='three'
          onClick={ this.handleGameClick }
          disabled={ three.length > 0 ? true : false }
          >{ three }</button>
          <button
          type='button'
          name='four'
          className='four'
          disabled={ four.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ four }</button>
          <button
          type='button'
          name='five'
          className='five'
          disabled={ five.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ five }</button>
          <button
          type='button'
          name='six'
          className='six'
          disabled={ six.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ six }</button>
          <button
          type='button'
          name='seven'
          className='seven'
          disabled={ seven.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ seven }</button>
          <button
          type='button'
          name='eight'
          className='eight'
          disabled={ eight.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ eight }</button>
          <button
          type='button'
          name='nine'
          className='nine'
          disabled={ nine.length > 0 ? true : false }
          onClick={ this.handleGameClick }
          >{ nine }</button>
        </div>
        </div>
        </div>
      </main>

      </div>
    )
  }
}

export default Game
