import { useContext } from 'react'
import { GameContext } from '../context';
import '../styles/pages/Game.css';

function Game() {
  const { game, setGame } = useContext(GameContext)
  const { selected, one, two, three, four, five, six, seven, eight, nine } = game

  const handleGameClick = ({ target }) => {
    const { name } = target;
    setGame({
      [name]: selected,
      selected: selected === 'X' ? 'O' : 'X',
    });
    checkWin();
  };

  const handleSelectO = () => {
    setGame({
      selected: 'O',
      three: 'X'
    })
  }

  const checkWin = () => {
    return one, two, three === 'X' ? console.log('WIN') : console.log('')
  }

  return (
    <div>

    <header>
      <h1>Jogo da Velha</h1>
    </header>

    <main>
      <div className='container'>
        <div className='difficulty-container'>
      <select
        className="difficulty"
        name="difficulty"
      >
        <option>Jogar contra um amigo</option>
        <option>Fácil</option>
        <option>Médio</option>
        <option>Impossível</option>
      </select>
      </div>
      <div className='players-container'>
      <button
        type='button'
        name='player-1-x'
        className={selected === 'X' ? 'player-1-x-selected' : 'player-1-x'}
        value='X'
        >X
      </button>
      <button
        type='button'
        name='player-2-o'
        className={selected === 'O' ? 'player-2-o-selected' : 'player-2-o'}
        value='O'
        onClick={ handleSelectO }
        >O
      </button>
      </div>
      <div>
        <span>Vez de {selected}</span>
      </div>
      <div className='game-container'>
      <div className='game'>
        <button
        type='button'
        name='one'
        className='one'
        style={ one === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ one.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ one }</button>
        <button
        type='button'
        name='two'
        className='two'
        style={ two === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ two.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ two }</button>
        <button
        type='button'
        name='three'
        className='three'
        style={ three === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        onClick={ handleGameClick }
        disabled={ three.length > 0 ? true : false }
        >{ three }</button>
        <button
        type='button'
        name='four'
        className='four'
        style={ four === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ four.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ four }</button>
        <button
        type='button'
        name='five'
        className='five'
        style={ five === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ five.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ five }</button>
        <button
        type='button'
        name='six'
        className='six'
        style={ six === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ six.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ six }</button>
        <button
        type='button'
        name='seven'
        className='seven'
        style={ seven === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ seven.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ seven }</button>
        <button
        type='button'
        name='eight'
        className='eight'
        style={ eight === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ eight.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ eight }</button>
        <button
        type='button'
        name='nine'
        className='nine'
        style={ nine === 'X' ? { color: '#545454' } : { color: '#f2ebd3'} }
        disabled={ nine.length > 0 ? true : false }
        onClick={ handleGameClick }
        >{ nine }</button>
      </div>
      </div>
      </div>
    </main>

    </div>
  )
}

export default Game
